namespace Core\Engine\Payment;

/**
 * Test class for Gateway.
 * Generated by PHPUnit on 2010-10-29 at 16:48:20.
 */
class GatewayTest extends \PHPUnit_Framework_TestCase {
    public function testProcessInvokesAdapterAndReturnsResponse() {
        $adapter = $this->getMockForAbstractClass('Core\Engine\Payment\Gateway\AdapterAbstract');
        $adapter->expects($this->once())
                ->method('process')
                ->with($this->isInstanceOf('\Core\Engine\Payment\Transaction'), $this->isType('array'))
                ->will($this->returnValue(new Gateway\Response(array(
                    'success' => true,
                    'transactionId' => 'foobar',
                ))));
        
        $gateway = new Gateway(array(
            'adapter' => $adapter,
        ));

        $response = $gateway->process(new Transaction(), array());

        $this->assertEquals('foobar', $response->getTransactionId(), 'Assert the proper response was returned by the mock adapter');
    }
}
